import{_ as e,c as l,o,a1 as i}from"./chunks/framework.yskgK-vV.js";const _=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"Other/小程序/组件/03.scroll-view滚动.md","filePath":"Other/小程序/组件/03.scroll-view滚动.md"}'),c={name:"Other/小程序/组件/03.scroll-view滚动.md"},r=i('<h2 id="scroll-view-滚动视图区域" tabindex="-1">scroll-view 滚动视图区域 <a class="header-anchor" href="#scroll-view-滚动视图区域" aria-label="Permalink to &quot;scroll-view 滚动视图区域&quot;">​</a></h2><h3 id="_01-关于滚动scroll" tabindex="-1">01. 关于滚动<code>scroll</code> <a class="header-anchor" href="#_01-关于滚动scroll" aria-label="Permalink to &quot;01. 关于滚动`scroll`&quot;">​</a></h3><ul><li><code>scroll-x</code>：允许横向滚动</li><li><code>scroll-y</code>：允许纵向滚动</li><li><code>scroll-top</code>：设置竖向滚动条位置</li><li><code>scroll-left</code>：设置横向滚动条位置</li><li><code>scroll-into-view</code>：值应为某子元素 id（id 不能以数字开头）。设置哪个方向可滚动，则在哪个方向滚动到该元素</li><li><code>scroll-with-animation</code>：在设置滚动条位置时使用动画过渡</li><li><code>scroll-anchoring</code>：开启 scroll anchoring 特性，即控制滚动位置不随内容变化而抖动，仅在 iOS 下生效，安卓下可参考 CSS overflow-anchor 属性。</li></ul><h3 id="_02-关于下拉刷新" tabindex="-1">02. 关于下拉刷新 <a class="header-anchor" href="#_02-关于下拉刷新" aria-label="Permalink to &quot;02. 关于下拉刷新&quot;">​</a></h3><ul><li><code>refresher-enabled</code>：开启自定义下拉刷新</li><li><code>refresher-threshold</code>：设置自定义下拉刷新阈值</li><li><code>refresher-default-style</code>：设置自定义下拉刷新默认样式，支持设置 black | white | none， none 表示不使用默认样式</li><li><code>refresher-background</code>：设置自定义下拉刷新区域背景颜色</li><li><code>refresher-triggered</code>：设置当前下拉刷新状态，true 表示下拉刷新已经被触发，false 表示下拉刷新未被触发</li></ul><h3 id="_03-其它属性" tabindex="-1">03. 其它属性 <a class="header-anchor" href="#_03-其它属性" aria-label="Permalink to &quot;03. 其它属性&quot;">​</a></h3><ul><li><p><code>enable-back-to-top</code>：iOS 点击顶部状态栏、安卓双击标题栏时，滚动条返回顶部，只支持竖向</p></li><li><p><code>enable-flex</code>：<strong>启用 flexbox 布局</strong>。开启后，当前节点声明了 display: flex 就会成为 flex container，并作用于其孩子节点。</p><blockquote><p>注意版本！！！</p></blockquote></li></ul><h3 id="_04-关于事件" tabindex="-1">04. 关于事件 <a class="header-anchor" href="#_04-关于事件" aria-label="Permalink to &quot;04. 关于事件&quot;">​</a></h3><ul><li><p><code>upper-threshold</code>：距顶部/左边多远时，触发 scrolltoupper 事件</p></li><li><p><code>lower-threshold</code>：距底部/右边多远时，触发 scrolltolower 事件</p></li><li><p><code>bindscrolltoupper</code>：滚动到顶部/左边时触发</p></li><li><p><code>bindscrolltolower</code>：滚动到底部/右边时触发</p></li><li><p><code>bindscroll</code>：滚动时触发</p><div class="language-js vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">js</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">event.detail </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> {scrollLeft, scrollTop, scrollHeight, scrollWidth, deltaX, deltaY}</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li><li><p><code>bindrefresherpulling</code>：自定义下拉刷新控件被下拉</p></li><li><p><code>bindrefresherrefresh</code>：自定义下拉刷新被触发</p></li><li><p><code>bindrefresherrestore</code>：自定义下拉刷新被复位</p></li><li><p><code>bindrefresherabo</code>：自定义下拉刷新被中止</p></li></ul><h3 id="_05-增强属性" tabindex="-1">05. 增强属性 <a class="header-anchor" href="#_05-增强属性" aria-label="Permalink to &quot;05. 增强属性&quot;">​</a></h3><ul><li><code>enhanced</code>：启用 scroll-view 增强特性，启用后可通过 ScrollViewContext 操作 scroll-view</li><li><code>show-scrollbar</code>：滚动条显隐控制 (同时开启 enhanced 属性后生效)</li><li><code>paging-enabled</code>：分页滑动效果 (同时开启 enhanced 属性后生效)</li><li><code>fast-deceleration</code>：滑动减速速率控制 (同时开启 enhanced 属性后生效)</li><li><code>bounces</code>：iOS 下 scroll-view 边界弹性控制 (同时开启 enhanced 属性后生效)</li><li><code>binddragstart</code>：滑动开始事件 (同时开启 enhanced 属性后生效)</li><li><code>binddragging</code>：滑动事件 (同时开启 enhanced 属性后生效)</li><li><code>binddragend</code>：滑动结束事件 (同时开启 enhanced 属性后生效)</li></ul>',11),a=[r];function d(s,t,n,h,p,u){return o(),l("div",null,a)}const f=e(c,[["render",d]]);export{_ as __pageData,f as default};
